import React, { useEffect, useState } from "react";

export default function PokedexEntry({ species }) {
    const [description, setDescription] = useState("");
    const [generation, setGeneration] = useState("");

    useEffect(() => {
        const fetchPokedexEntry = async () => {
          try {
            const response = await fetch(species.pokedex_numbers[0].url);
            const data = await response.json();

            const descriptionEntries = data.flavor_text_entries;
            const englishDescription = descriptionEntries.find(
              (entry) => entry.language.name === "en"
            );
            setDescription(englishDescription.flavor_text);
          } catch (error) {
            console.log("Error fetching Pokedex entry:", error);
          }
        };

        fetchPokedexEntry();
      }, [species.pokedex_numbers]);      

    return (
      <div>
        <h2>Pokedex Entry - Generation {generation}</h2>
        <p>{description}</p>
      </div>
    );
  };
