import { useState, useEffect } from 'react';
import axios from 'axios';

export default function Search() {
  const [name, setName] = useState('');
  const [primaryType, setPrimaryType] = useState('');
  const [secondaryType, setSecondaryType] = useState('');
  const [types, setTypes] = useState([]);
  const [generations, setGenerations] = useState([]);

  useEffect(() => {
    const fetchTypes = async () => {
      try {
        const response = await axios.get('https://pokeapi.co/api/v2/type');
        const types = response.data.results.map((type) => type.name);
        setTypes(types);
      } catch (error) {
        console.log('Error:', error.message);
      }
    };

    const fetchGenerations = async () => {
      try {
        const response = await axios.get('https://pokeapi.co/api/v2/generation');
        const generations = response.data.results.map((generation) => generation.name);
        setGenerations(generations);
      } catch (error) {
        console.log('Error:', error.message);
      }
    };

    fetchTypes();
    fetchGenerations();
  }, []);

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handlePrimaryTypeChange = (event) => {
    setPrimaryType(event.target.value);
  };

  const handleSecondaryTypeChange = (event) => {
    setSecondaryType(event.target.value);
  };

  return (
    <div>
      <div>
        <div>
          <input value={name} onChange={handleNameChange}></input>
        </div>
        <div>
          <h3>Primary Type: </h3>
          <select value={primaryType} onChange={handlePrimaryTypeChange}>
            <option value="">All</option>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Secondary Type: </h3>
          <select value={secondaryType} onChange={handleSecondaryTypeChange}>
            <option value="">All</option>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Generation: </h3>
          <select>
            {generations.map((gen) => (
              <option key={gen}>{gen}</option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
}
