import { useState } from 'react'
import { capitalizeLetter } from '../../utils';

function Pagination({ currentPage, totalPages, onPageChange }) {
  const pageNumbers = Array.from({ length: totalPages }, (_, i) => i + 1);

  return (
    <div>
      {pageNumbers.map(page => (
        <button key={page} onClick={() => onPageChange(page)} disabled={page === currentPage}>
          {page}
        </button>
      ))}
    </div>
  );
}

export default function Pokedex({ pokedex, setPokemonData }) {
  const itemsPerPage = 20; // Number of items to display per page
  const [pageNum, setPageNum] = useState(1);

  const startIndex = (pageNum - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentPokedex = pokedex.slice(startIndex, endIndex);

  const maxPageNum = Math.ceil(pokedex.length / itemsPerPage);

  const goToPage = (page) => {
    setPageNum(page);
  }

  const goToPrev = () => {
    setPageNum(prevPageNum => Math.max(prevPageNum - 1, 1));
  }

  const goToNext = () => {
    setPageNum(prevPageNum => Math.min(prevPageNum + 1, maxPageNum));
  }

  return (
    <div>
      <div>
        <button onClick={goToPrev} disabled={pageNum === 1}>Previous</button>
        <Pagination
          currentPage={pageNum}
          totalPages={maxPageNum}
          onPageChange={goToPage}
        />
        <button onClick={goToNext} disabled={pageNum === maxPageNum}>Next</button>
      </div>
      {currentPokedex.map(p => (
        <div key={p.name} onClick={() => setPokemonData(p.name)}>
          <h3>{capitalizeLetter(p.name)}</h3>
          <img src={p.sprites.front_default} alt={p.name} />
        </div>
      ))}
    </div>
  )
}
