import { useState, useEffect } from 'react';
import { capitalizeLetter } from '../../utils';
import Type from '../../components/Type';

export default function Pokedex({ pokedex, setPokemonData, page, handlePage }) {
  const [pageIndx, setPageIndx] = useState([])
  const totalPages = Math.ceil(1019 / pokedex.length);
  const pageNumbers = Array.from({ length: totalPages }, (_, index) => index + 1);

  useEffect(() => {
    const indxNum = page + 9;
    const indxArr = Array.from({ length: indxNum }, (_, index) => index + 1);

    handlePageIndx(pageNumbers, indxArr)

  }, [page]);

  const handlePageIndx = (pageArr, listArr) => {
    for (let i = 0; i <= pageArr; i++) {
      for (let y = 0; y <= listArr; y++) {
        if (i === y && i >= page) {
          console.log(y);
        }
      }
    }
  }

  console.log(page);

  return (
    <div className='PokemonList'>
      <div>
        <button onClick={() => handlePage('prev')}>Previous</button>
        <button onClick={() => handlePage('prevTen')}>{'<<'}</button>
        {/* {pageArray.map((pageNumber) => (
          <div key={pageNumber}>{pageNumber}</div>
        ))} */}
        <button onClick={() => handlePage('next')}>Next</button>
        <button onClick={() => handlePage('nextTen')}>{'>>'}</button>
      </div>

      <div className='pokedex'>
        {pokedex.map(p => (
          <div className={`pokedex-item ${p.types[0].type.name}`} key={p.name} onClick={() => setPokemonData(p.name)}>
            <h3>{capitalizeLetter(p.name)}</h3>
            <img src={p.sprites.front_default} alt={p.name} />
            <div className='type-container'>
              {p.types.map(types => (
                <Type type={types.type.name} key={types.type.name} />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
