import { useState, useEffect } from 'react';
import axios from 'axios';

import Search from './components/Search';
import PokemonList from './components/PokemonListData/PokemonList';
import PokemonData from './components/PokemonData/Pokemon';
import './App.css';

function App() {
  const [pokedex, setPokedex] = useState([]);
  const [pokemon, setPokemon] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        setLoading(true);
        const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=20');
        const pokemonResults = response.data.results;
        const pokemonDataPromises = pokemonResults.map((result) => axios.get(result.url));
        const pokemonDataResponses = await Promise.all(pokemonDataPromises);
        const pokemonData = pokemonDataResponses.map((res) => res.data);
        setPokedex(pokemonData);
        setLoading(false);
      } catch (error) {
        console.log('Error:', error.message);
        setLoading(false);
      }
    };

    fetchPokemonData();
  }, []);

  const handleSearch = async (name) => {
    try {
      setLoading(true);

      if (name === '') {
        const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=20');
        const pokemonResults = response.data.results;
        const pokemonDataPromises = pokemonResults.map((result) => axios.get(result.url));
        const pokemonDataResponses = await Promise.all(pokemonDataPromises);
        const pokemonData = pokemonDataResponses.map((res) => res.data);
        setPokedex(pokemonData);
      } else {
        const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${name}`);
        const pokemonData = response.data;
        setPokedex([pokemonData]);
      }

      setLoading(false);
    } catch (error) {
      console.log('Error:', error.message);
      setLoading(false);
    }
  };

  const setPokemonData = (pokemonData) => {
    setPokemon(pokemonData);
  };

  const formatHome = () => {
    setPokemon(null);
  };

  return (
    <div className="App">
      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          {pokemon ? (
            <PokemonData pokemon={pokemon} setPokemonData={setPokemonData} formatHome={formatHome} />
          ) : (
            <>
              <Search handleSearch={handleSearch} />
              <PokemonList pokedex={pokedex} setPokemonData={setPokemonData} />
            </>
          )}
        </>
      )}
    </div>
  );
}

export default App;
