import { useState, useEffect } from 'react';
import axios from 'axios';

import Pokemon from './components/Pokemon';
import './App.css';

function App() {
  const [pokemon, setPokemon] = useState([])
  const [url, setUrl] = useState('https://pokeapi.co/api/v2/pokemon?offset=0&limit=40')

  useEffect(() => {
    let cancel
    axios.get(url, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    })
    .then(res => {
      setPokemon(res.data.results.map(p => p.name))
      console.log(res.data)
    })
    .catch(error => {
      console.log('Error: ', error.message)
    })
    return () => cancel()
  }, [url])

  return (
    <div className="App">
      <div>
        <button onClick={() => {
          setUrl(res.data.previous)
        }}>Previous</button>
        <button>Next</button>
      </div>
        <Pokemon pokemon={pokemon}/>
    </div>
  );
}

export default App;
