import React from 'react';
import capitalizeLetter from '../utils';

export default function Pokemon({ pokemon, formatHome }) {
  const secondType = pokemon.types.length > 1 ? pokemon.types[1].type.name : null;

  const formatMeter = (h) => {
    return (h * 0.1).toFixed(1)
  }

  function formatFeet(meters) {
    const totalInches = meters * 39.37; 
    const feet = Math.floor(totalInches / 12); 
    const inches = Math.round(totalInches % 12); 

    return { feet, inches };
  }


  return (
    <div>
      <h2>{capitalizeLetter(pokemon.name)}</h2>
      <img src={pokemon.sprites.front_default} alt={pokemon.name} />
      <div>
        <h3>Type</h3>
        <p>{pokemon.types[0].type.name}</p>
        {secondType && <p>{secondType}</p>}
      </div>
      <div>
        <h3>Height</h3>
        <p>{formatMeter(pokemon.height)}m</p>
        <h3>Weight</h3>
        <p>{formatFeet(formatMeter)}</p>
      </div>
      <button onClick={formatHome}>Back</button>
    </div>
  );
}
