import { useEffect, useState } from 'react';
import axios from 'axios';

import { capitalizeLetter } from '../../utils';

import EvolutionImg from './EvolutionImg';

export default function Evolution({ pokemon, species }) {
  const [evolutionData, setEvolutionData] = useState(null);

  useEffect(() => {
    const fetchEvolutionData = async () => {
      try {
        const response = await axios.get(species.evolution_chain.url);
        const newData = response.data;
        setEvolutionData(newData);
        console.log(newData);
      } catch (error) {
        console.log('Error: ', error.message);
      }
    };

    fetchEvolutionData();
  }, [species]);

  const renderEvolutionChain = (chain) => {
    return (
      <div>
        <div>
          <h3>{capitalizeLetter(chain.species.name)}</h3>
          {chain.evolution_details && chain.evolution_details.length > 0 && (
            <p>
              Evolution Method: {getEvolutionMethod(chain.evolution_details[0])}
            </p>
          )}
          <EvolutionImg
            imageUrl={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${chain.species.url.split('/')[6]}.png`}
          />
          {chain.evolves_to.length > 0 && (
            <div>
              {chain.evolves_to.map((evo) => (
                <div key={evo.species.name}>
                  <h3>{capitalizeLetter(evo.species.name)}</h3>
                  {evo.evolution_details && evo.evolution_details.length > 0 && (
                    <p>
                      Evolution Method: {getEvolutionMethod(evo.evolution_details[0])}
                    </p>
                  )}
                  <EvolutionImg
                    imageUrl={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${evo.species.url.split('/')[6]}.png`}
                  />
                  {evo.evolves_to.length > 0 && renderEvolutionChain(evo.evolves_to[0])}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  };

  const getEvolutionMethod = (evolutionDetail) => {
    const trigger = evolutionDetail.trigger.name;

    switch (trigger) {
      case 'level-up':
        return evolutionDetail.min_level ? `Level ${evolutionDetail.min_level}` : 'Level Up';

      case 'use-item':
        return `Item: ${capitalizeLetter(evolutionDetail.item.name)}`;

      case 'trade':
        return 'Trade';

      case 'other':
        if (evolutionDetail.held_item) {
          return `Held Item: ${capitalizeLetter(evolutionDetail.held_item.name)}`;
        }

        if (evolutionDetail.min_affection) {
          return 'Friendship';
        }

        if (evolutionDetail.time_of_day) {
          if (evolutionDetail.time_of_day === 'day') {
            return 'Friendship (Daytime)';
          }
          if (evolutionDetail.time_of_day === 'night') {
            return 'Friendship (Nighttime)';
          }
        }

        if (evolutionDetail.known_move_type) {
          const moveType = capitalizeLetter(evolutionDetail.known_move_type.name);
          return `Learn ${moveType} move`;
        }

        if (evolutionDetail.party_species) {
          const partySpecies = capitalizeLetter(evolutionDetail.party_species.name);
          return `Level up with ${partySpecies} in the party`;
        }

        if (evolutionDetail.location) {
          const locationName = capitalizeLetter(evolutionDetail.location.name);
          return `Level up at ${locationName}`;
        }

        if (evolutionDetail.trigger.name === 'level-up' && evolutionDetail.gender === 0 && evolutionDetail.held_item === null && evolutionDetail.known_move_type === null && evolutionDetail.min_affection === null && evolutionDetail.min_beauty === null && evolutionDetail.min_happiness === null && evolutionDetail.needs_overworld_rain === null && evolutionDetail.party_species === null && evolutionDetail.party_type === null && evolutionDetail.relative_physical_stats === null && evolutionDetail.time_of_day === null && evolutionDetail.trade_species === null && evolutionDetail.turn_upside_down === null) {
          return 'Friendship';
        }

        break;

      default:
        return 'Unknown';
    }
  };



  return (
    <>
      <h2>Evolution Chain</h2>
      {evolutionData ? (
        evolutionData.chain ? (
          renderEvolutionChain(evolutionData.chain)
        ) : (
          <p>No Evolution</p>
        )
      ) : (
        <p>Loading evolution chain...</p>
      )}
    </>
  );
}
