import { useState } from 'react';

export default function Search({ handleSearch }) {
  const [name, setName] = useState('');
  const [primaryType, setPrimaryType] = useState('');
  const [secondaryType, setSecondaryType] = useState('');
  const [generation, setGeneration] = useState('');

  const types = ["All", "Normal", "Fire", "Water", "Electric", "Grass", "Ice", "Fighting", "Poison", "Ground", "Flying", "Psychic", "Bug", "Rock", "Ghost", "Dragon", "Dark", "Steel"];

  const generations = ["All", "I", "II", "III", "IV", "V", "VI", "VII", "VIII", "IX"];

  const handlePrimaryType = (e) => {
    setPrimaryType(e.target.value);
  };

  const handleSecondaryType = (e) => {
    setSecondaryType(e.target.value);
  };

  const handleGeneration = (e) => {
    setGeneration(e.target.value);
  };

  return (
    <div>
      <div>
        <div>
          <input value={name} onChange={(e) => {
            const searchValue = e.target.value;
            setName(searchValue);
            handleSearch(searchValue);
          }}></input>
        </div>
        <div>
          <h3>Primary Type: </h3>
          <select value={primaryType} onChange={handlePrimaryType}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Secondary Type: </h3>
          <select value={secondaryType} onChange={handleSecondaryType}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Generation: </h3>
          <select value={generation} onChange={handleGeneration}>
            {generations.map((gen) => (
              <option key={gen}>{gen}</option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
}
