import { useState, useEffect } from 'react';
import axios from 'axios';

import Pokedex from './components/Pokedex';
import Pokemon from './components/Pokemon';
import './App.css';

function App() {
  const [pokedex, setPokedex] = useState([]);
  const [pokemon, setPokemon] = useState('');

  const [url, setUrl] = useState('https://pokeapi.co/api/v2/pokemon/');
  const [loading, setLoading] = useState(true);

  const [seeMore, setSeeMore] = useState('');
  const [next, setNext] = useState('');
  const [prev, setPrev] = useState('');

  useEffect(() => {
    let cancel;
    setLoading(true);
    axios
      .get(url, {
        cancelToken: new axios.CancelToken(c => (cancel = c))
      })
      .then(res => {
        setSeeMore(res.data.next);
        setPrev(res.data.previous);
        setNext(res.data.next);
        getPokemonData(res.data.results);
        console.log(pokedex);
        setLoading(false);
      })
      .catch(error => {
        console.log('Error: ', error.message);
        setLoading(false);
      });
    return () => cancel();
  }, [url]);

  const getPokemonData = async (res) => {
    if (res) {
      const pokemonDataPromises = res.map((p) => axios.get(p.url));
      try {
        const results = await Promise.all(pokemonDataPromises);
        const pokemonData = results.map((res) => res.data);
        setPokedex((prevPokedex) => [...prevPokedex, ...pokemonData]);
      } catch (error) {
        console.log('Error:', error.message);
      }
    }
  };


  const goToPrev = () => {
    setPokedex([]);
    setUrl(prev);
  };

  const goToNext = () => {
    setPokedex([]);
    setUrl(next);
  };

  const formatSeeMore = () => {
    setUrl(seeMore);
  };

  const setPokemonData = p => {
    setPokemon(p);
    console.log(p);
  };

  useEffect(() => {
    if (pokemon) {
      setUrl(`https://pokeapi.co/api/v2/pokemon/${pokemon}`);
    }
  }, [pokemon]);


  return (
    <div className="App">
      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <Pokedex
            pokedex={pokedex}
            setPokemonData={setPokemonData}
            formatSeeMore={formatSeeMore}
            goToPrev={goToPrev}
            goToNext={goToNext}
          />
          {pokemon ? <Pokemon /> : null}
        </>
      )}
    </div>
  );
}

export default App;
