import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MoveList({ url }) {
  const [moves, setMoves] = useState([]);

  useEffect(() => {
    async function fetchMoveList() {
      try {
        const response = await axios.get(url);
        const { moves } = response.data;
        setMoves(moves);
      } catch (error) {
        console.error('Error fetching move list:', error);
      }
    }

    fetchMoveList();
  }, [url]);

  // Group moves based on learning method
  const groupedMoves = {};
  moves.forEach(move => {
    const method = move.version_group_details[0].move_learn_method.name;
    if (!groupedMoves[method]) {
      groupedMoves[method] = [];
    }
    groupedMoves[method].push(move.move.name);
  });

  return (
    <div>
      <h2>Move List</h2>
      {Object.entries(groupedMoves).map(([method, moves]) => (
        <div key={method}>
          <h3>{method}</h3>
          <ul>
            {moves.map(move => (
              <li key={move}>{move}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}
