import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Evolution({ pokemon, species}) {
  const [evolutionData, setEvolutionData] = useState(null)  

  useEffect(() => {
    const fetchNewData = async (data) => {
      try {
        if (pokemon && pokemon.species ) {
          const response = await axios.get(species.evolution_chain.url);
          const newData = response.data;
          setEvolutionData(newData);
          console.log(newData);
        }
      } catch (error) {
        console.log('Error: ', error.message);
      }
    };

    fetchNewData();
  }, [species]);

  return (
    <>
        {evolutionData.evolves_to ? 
        (<div>
            <h2>Evolution Chain</h2>
            <div>
                <p>{evolutionData.evolves_to.map(evo => (<p></p>))}</p>
            </div>
        </div>) : 
        <p>No Evolution</p>}
    </>
  )
}
