import { useState, useEffect} from 'react'

export default function Search() {
  const [name, setName] = useState('');
  const [primaryType, setPrimaryType] = useState('');
  const [secondaryType, setSecondaryType] = useState('');

  const types = ["All", "Normal", "Fire", "Water", "Electric", "Grass", "Ice", "Fighting", "Poison", "Ground", "Flying", "Psychic", "Bug", "Rock", "Ghost", "Dragon", "Dark", "Steel"];

  const generation = ["All", "I", "II", "III", "IV", "V", "VI", "VII", "VIII", "IX"];

  const handleName = (e) => {
    setName(e.target.value)
  }

  return (
    <div>
      <div>
        <div>
          <input onChange={handleName}></input>
        </div>
        <div>
          <h3>Primary Type: </h3>
          <select>
            {types.map(type => (
              <option>{type}</option>
            ))}
          </select>
        </div>
        <div>
          <h3>Secondary Type: </h3>
          <select>
            {types.map(type => (
              <option>{type}</option>
            ))}
          </select>
        </div>
        <div>
          <h3>Generation: </h3>
          <select>
            {generation.map(gen => (
              <option>{gen}</option>
            ))}
          </select>
        </div>
      </div>
    </div>
  )
}
