import React, { useState } from 'react';
import axios from 'axios';

import capitalizeLetter from '../utils';

export default function Pokemon({ pokemon, formatGen }) {
  const [pokemonData, setPokemonData] = useState(null);

  const secondType = pokemon.types.length > 1 ? pokemon.types[1].type.name : null;

  const handleFetchData = async () => {
    try {
      const response = await axios.get(pokemon.species.url);
      const newData = response.data; // Modify this line to extract the desired data from the API response
      setPokemonData(newData);
    } catch (error) {
      console.log('Error:', error.message);
    }
  };

  return (
    <div>
      <h2>{capitalizeLetter(pokemon.name)}</h2>
      <img src={pokemon.sprites.front_default} alt={pokemon.name} />
      <div>
        <p>Type</p>
        <p>{pokemon.types[0].type.name}</p>
        {secondType && <p>{secondType}</p>}
      </div>
      <div>
        <button onClick={handleFetchData}>Fetch Data</button>
      </div>
      {pokemonData && (
        <div>
          {/* Display the retrieved data here */}
          <p>{/* Add the data you want to display here */}</p>
        </div>
      )}
    </div>
  );
}
