import { useState, useEffect } from 'react';
import axios from 'axios';

export default function Search({ handleSearch }) {
  const [name, setName] = useState('');
  const [primaryType, setPrimaryType] = useState('All');
  const [secondaryType, setSecondaryType] = useState('All');
  const [generation, setGeneration] = useState('All');
  const [filteredPokemon, setFilteredPokemon] = useState([]);

  const types = ['All', 'Normal', 'Fire', 'Water', 'Electric', 'Grass', 'Ice', 'Fighting', 'Poison', 'Ground', 'Flying', 'Psychic', 'Bug', 'Rock', 'Ghost', 'Dragon', 'Dark', 'Steel'];

  const generations = ['All', 'I', 'II', 'III', 'IV', 'V', 'VI', 'VII', 'VIII', 'IX'];

  useEffect(() => {
    const filterPokemon = () => {
      const filteredPokedex = filteredPokemon.filter((pokemon) => {
        const isMatchingName = pokemon.name.toLowerCase().includes(name.toLowerCase());
        const isMatchingPrimaryType = primaryType === 'All' || pokemon.types.includes(primaryType);
        const isMatchingSecondaryType = secondaryType === 'All' || pokemon.types.includes(secondaryType);
        const isMatchingGeneration = generation === 'All' || pokemon.generation === generation;
        return isMatchingName && isMatchingPrimaryType && isMatchingSecondaryType && isMatchingGeneration;
      });

      setFilteredPokemon(filteredPokedex);
    };

    filterPokemon();
  }, [name, primaryType, secondaryType, generation, filteredPokemon]);

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=100000&offset=0');
        const pokemonResults = response.data.results;
        const pokemonDataPromises = pokemonResults.map((result) => axios.get(result.url));
        const pokemonDataResponses = await Promise.all(pokemonDataPromises);
        const pokemonData = pokemonDataResponses.map((res) => res.data);
        setFilteredPokemon(pokemonData);
      } catch (error) {
        console.log('Error:', error.message);
      }
    };

    fetchPokemonData();
  }, []);

  return (
    <div>
      <div>
        <div>
          <input value={name} onChange={(e) => setName(e.target.value)}></input>
        </div>
        <div>
          <h3>Primary Type: </h3>
          <select value={primaryType} onChange={(e) => setPrimaryType(e.target.value)}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Secondary Type: </h3>
          <select value={secondaryType} onChange={(e) => setSecondaryType(e.target.value)}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Generation: </h3>
          <select value={generation} onChange={(e) => setGeneration(e.target.value)}>
            {generations.map((gen) => (
              <option key={gen}>{gen}</option>
            ))}
          </select>
        </div>
      </div>
      <div>
        {filteredPokemon.map((pokemon) => (
          <div key={pokemon.id}>
            <img src={pokemon.sprites.front_default} alt={pokemon.name} />
            <p>{pokemon.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
