import React, { useEffect, useState } from "react";

export default function PokedexEntry({ speciesUrl }) {
  const [pokedexEntries, setPokedexEntries] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPokedexEntries = async () => {
      try {
        const response = await fetch(speciesUrl);
        if (!response.ok) {
          throw new Error("Failed to fetch Pokedex entries");
        }
        const data = await response.json();
        setPokedexEntries(data.flavor_text_entries);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchPokedexEntries();
  }, [speciesUrl]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h2>Pokedex Entry</h2>
      {pokedexEntries.map((entry, index) => (
        <p key={index}>{entry.flavor_text}</p>
      ))}
    </div>
  );
}
