import { useEffect, useState } from 'react';
import axios from 'axios';

import { capitalizeLetter } from '../../utils';

export default function Evolution({ species }) {
  const [evolutionData, setEvolutionData] = useState(null);

  useEffect(() => {
    const fetchEvolutionData = async () => {
      try {
        const response = await axios.get(species.evolution_chain.url);
        const newData = response.data;
        setEvolutionData(newData);
      } catch (error) {
        console.log('Error: ', error.message);
      }
    };

    fetchEvolutionData();
  }, [species]);

  const renderEvolutionChain = (chain) => {
    const evolutions = [];
    let currentChain = chain;

    while (currentChain && currentChain.species) {
      evolutions.push({
        name: capitalizeLetter(currentChain.species.name),
        image: `https://pokeapi.co/media/sprites/pokemon/${currentChain.species.url.split('/').slice(-2, -1)}.png`
      });

      if (currentChain.evolves_to.length > 0) {
        const nextChain = currentChain.evolves_to[0];
        if (nextChain.evolution_details[0]?.min_level === null) {
          evolutions.push({
            name: capitalizeLetter(nextChain.species.name),
            image: `https://pokeapi.co/media/sprites/pokemon/${nextChain.species.url.split('/').slice(-2, -1)}.png`
          });
        }
        currentChain = nextChain;
      } else {
        break;
      }
    }

    return evolutions.map((evolution, index) => (
      <div key={index}>
        <img src={evolution.image} alt={evolution.name} />
        <p>{evolution.name}</p>
      </div>
    ));
  };

  return (
    <>
      <h2>Evolution Chain</h2>
      {evolutionData ? (
        evolutionData.chain ? (
          renderEvolutionChain(evolutionData.chain)
        ) : (
          <p>No Evolution</p>
        )
      ) : (
        <p>Loading evolution chain...</p>
      )}
    </>
  );
}
