import { useState, useEffect } from 'react';
import axios from 'axios';

export default function Search() {
  const [name, setName] = useState('');
  const [primaryType, setPrimaryType] = useState('');
  const [secondaryType, setSecondaryType] = useState('');
  const [generation, setGeneration] = useState('');
  const [filteredPokemon, setFilteredPokemon] = useState([]);

  const types = ["All", "Normal", "Fire", "Water", "Electric", "Grass", "Ice", "Fighting", "Poison", "Ground", "Flying", "Psychic", "Bug", "Rock", "Ghost", "Dragon", "Dark", "Steel"];

  const generations = ["All", "I", "II", "III", "IV", "V", "VI", "VII", "VIII", "IX"];

  useEffect(() => {
    const fetchFilteredPokemon = async () => {
      try {
        let url = 'https://pokeapi.co/api/v2/pokemon';

        // Build the request parameters
        const params = {};

        if (name) {
          params.name = name.toLowerCase();
        }

        if (primaryType && primaryType !== 'All') {
          params.types = primaryType.toLowerCase();
        }

        if (generation && generation !== 'All') {
          params.generation = parseInt(generation, 10);
        }

        // Make the API request
        const response = await axios.get(url, {
          params,
        });

        const pokemonResults = response.data.results;

        // Fetch data for each filtered Pokemon
        const fetchDataPromises = pokemonResults.map((result) => axios.get(result.url));
        const fetchedDataResponses = await Promise.all(fetchDataPromises);
        const fetchedData = fetchedDataResponses.map((res) => res.data);

        setFilteredPokemon(fetchedData);
      } catch (error) {
        console.log('Error:', error.message);
      }
    };

    fetchFilteredPokemon();
  }, [name, primaryType, generation]);

  const handleName = (e) => {
    setName(e.target.value);
  };

  const handlePrimaryType = (e) => {
    setPrimaryType(e.target.value);
  };

  const handleSecondaryType = (e) => {
    setSecondaryType(e.target.value);
  };

  const handleGeneration = (e) => {
    setGeneration(e.target.value);
  };

  return (
    <div>
      <div>
        <div>
          <input value={name} onChange={handleName}></input>
        </div>
        <div>
          <h3>Primary Type: </h3>
          <select value={primaryType} onChange={handlePrimaryType}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Secondary Type: </h3>
          <select value={secondaryType} onChange={handleSecondaryType}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Generation: </h3>
          <select value={generation} onChange={handleGeneration}>
            {generations.map((gen) => (
              <option key={gen}>{gen}</option>
            ))}
          </select>
        </div>
      </div>
      <div>
        {filteredPokemon.map((pokemon) => (
          <div key={pokemon.id}>
            <img src={pokemon.sprites.front_default} alt={pokemon.name} />
            <p>{pokemon.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
