import { useState } from 'react';

export default function Search({ handleSearch }) {
  const [name, setName] = useState('');

  const handleInput = (e) => {
    e.preventDefault();

    handleSearch(name)
  }

  return (
    <div>
      <form onSubmit={handleInput}>
        <input value={name} onChange={(e) => setName(e.target.value)}></input>
      </form>
    </div>
    )
}
