import { useState, useEffect } from 'react';
import axios from 'axios';

import { capitalizeLetter } from '../../utils';

import Type from '../../components/Type';

export default function Pokedex({ pokedex, setPokemonData, page, handleNewPage }) {
  const [currentPage, setCurrentPage] = useState(page)
  const [pageIndx, setPageIndx] = useState([]);
  const [totalIndx, setTotalIndx] = useState([]);
  const totalPages = Math.ceil(1019 / pokedex.length);
  const pageNumbers = Array.from({ length: totalPages }, (_, index) => index + 1);

  useEffect(() => {
    const indxNum = page + 9;
    const indxArr = Array.from({ length: indxNum }, (_, index) => index + 1);
    const num = 2;

    console.log(formatIdxNum(1200));
    handlePageIndx(totalIndx, indxArr);
    console.log(handlePageIndx(pageNumbers, indxArr));
  }, [page]);

  const handlePageIndx = (pageArr, listArr) => {
    const indxArr = [];
    const remainder = totalPages - totalPages % 10;

    for (let i = 0; i <= pageArr.length; i++) {
      for (let y = 0; y <= listArr.length; y++) {
        if (i === y && i >= page) {
          indxArr.push(y);
        }
      }
    }

    setPageIndx(indxArr);
    return indxArr;
  };  

  const formatIdxNum = (idx) => {
    const arr = [idx].toString().split('');

    for (let i =0; i < 5; i++) {
      if (arr.length > 4) {
        break;
      } else if (arr.length < 4) {
        arr.unshift('0')
      }
    }

    return arr
  }


  return (
    <div className='PokemonList'>
      <div>
        <button onClick={() => handleNewPage(page - 10)}>{'<<'}</button>
        <button onClick={() => handleNewPage(page - 1)}>Previous</button>
        {pageIndx.map((num) => (
          <div className={`number ${num == page ? 'current' : null}`} key={num}>{num + 1}</div>
        ))}
        <button onClick={() => handleNewPage(page + 1)}>Next</button>
        <button onClick={() => handleNewPage(page + 10)}>{'>>'}</button>
      </div>

      <div className='pokedex'>
        {pokedex.map(p => (
          <div className={`pokedex-item ${p.types[0].type.name}`} key={p.name} onClick={() => setPokemonData(p.name)}>
            <h3>{capitalizeLetter(p.name)}</h3>
            <img src={p.sprites.front_default} alt={p.name} />
            <div className='type-container'>
              {p.types.map(types => (
                <Type type={types.type.name} key={types.type.name} />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
