import { useState } from 'react';
import { capitalizeLetter } from '../../utils';
import Type from '../../components/Type';

export default function Pokedex({ pokedex, setPokemonData }) {
  const resultsPerPage = 20; // Number of Pokemon to display per page
  const [currentPage, setCurrentPage] = useState(0);

  // Calculate the start and end index of the Pokemon to display
  const startIndex = currentPage * resultsPerPage;
  const endIndex = startIndex + resultsPerPage;

  // Slice the pokedex array based on the current page
  const displayedPokemon = pokedex.slice(startIndex, endIndex);

  // Handle pagination navigation
  const goToPage = (page) => {
    setCurrentPage(page);
  };

  const goToPrevPage = () => {
    setCurrentPage(prevPage => Math.max(0, prevPage - 1));
  };

  const goToNextPage = () => {
    setCurrentPage(prevPage => Math.min(prevPage + 1, Math.ceil(pokedex.length / resultsPerPage) - 1));
  };

  // Calculate the total number of pages
  const totalPages = Math.ceil(pokedex.length / resultsPerPage);

  return (
    <div className='PokemonList'>
      <div>
        <button onClick={goToPrevPage} disabled={currentPage === 0}>
          Previous
        </button>
        {Array.from({ length: totalPages }, (_, index) => (
          <button key={index} onClick={() => goToPage(index)} disabled={currentPage === index}>
            {index + 1}
          </button>
        ))}
        <button onClick={goToNextPage} disabled={endIndex >= pokedex.length}>
          Next
        </button>
      </div>
      <div className='pokedex'>
        {displayedPokemon.map(p => (
          <div className={`pokedex-item ${p.types[0].type.name}`} key={p.name} onClick={() => setPokemonData(p.name)}>
            <h3>{capitalizeLetter(p.name)}</h3>
            <img src={p.sprites.front_default} alt={p.name} />
            <div className='type-container'>
              {p.types.map(types => (
                <Type type={types.type.name} key={types.type.name} />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
