import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Search({ handleSearch, pokedex }) {
  const [pokemonResults, setPokemonResults] = useState([])
  const [name, setName] = useState('');
  const [primaryType, setPrimaryType] = useState('');
  const [secondaryType, setSecondaryType] = useState('');
  const [generation, setGeneration] = useState('');
  const [filteredPokemon, setFilteredPokemon] = useState([]);

  const types = ["All", "Normal", "Fire", "Water", "Electric", "Grass", "Ice", "Fighting", "Poison", "Ground", "Flying", "Psychic", "Bug", "Rock", "Ghost", "Dragon", "Dark", "Steel"];

  const generations = ["All", "I", "II", "III", "IV", "V", "VI", "VII", "VIII", "IX"];

  // useEffect(() => {
  //   const fetchData = async () => {
  //     try {
  //       const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=20')
  //       const pokemonResults = response.data.results;
  //       const pokemonDataPromises = pokemonResults.map((result) => axios.get(result.url));
  //       const pokemonDataResponses = await Promise.all(pokemonDataPromises);
  //       const pokemonData = pokemonDataResponses.map((res) => res.data);
  //       setPokemonResults(pokemonData)
  //       handleSearch(pokem)
  //       console.log(pokemonResults);
  //     } catch (error) {
  //       console.log('Error:', error.message);
  //     }
  //   };
  //   fetchData();
  // }, [name]);

  // const handleName = (e) => {
  //   const filteredPokedex = pokemonResults.filter((pokemon) =>
  //     pokemon.name.toLowerCase().includes(e.target.value.toLowerCase())
  //   );
  //   setFilteredPokemon(filteredPokedex);
  //   setName(e.target.value);
  //   handleSearch(e.target.value);
  // };

  const handlePrimaryType = (e) => {
    setPrimaryType(e.target.value);
  };

  const handleSecondaryType = (e) => {
    setSecondaryType(e.target.value);
  };

  const handleGeneration = (e) => {
    setGeneration(e.target.value);
  };

  return (
    <div>
      <div>
        <div>
          <input value={name} onChange={handleName}></input>
        </div>
        <div>
          <h3>Primary Type: </h3>
          <select value={primaryType} onChange={handlePrimaryType}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Secondary Type: </h3>
          <select value={secondaryType} onChange={handleSecondaryType}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Generation: </h3>
          <select value={generation} onChange={handleGeneration}>
            {generations.map((gen) => (
              <option key={gen}>{gen}</option>
            ))}
          </select>
        </div>
      </div>
      <div>
        {filteredPokemon.map((pokemon) => (
          <div key={pokemon.id}>
            <img src={pokemon.sprites.front_default} alt={pokemon.name} />
            <p>{pokemon.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
