import { useState, useEffect } from 'react';
import { capitalizeLetter } from '../../utils';
import Type from '../../components/Type';

export default function Pokedex({ pokedex, setPokemonData, goToPrev, goToNext }) {
  const [page, setPage] = useState(1);
  const [pageIndx, setPageIndx] = useState(1);
  const [indxNumbers, setIndxNumbers] = useState([]);
  const results = pokedex.length;
  const totalPages = Math.ceil(1019 / results);
  const pageNumbers = Array.from({ length: totalPages }, (_, index) => index + 1);

  useEffect(() => {
    const numIndx = pageIndx + 9;
    setIndxNumbers(Array.from({ length: numIndx }, (_, index) => index + 1));
  }, [pageIndx])

  const handleNextPageSet = () => {
    setPageIndx(+ 10)
  }

  const handlePrevPageSet = () => {
    setPageIndx(- 10)
  }


  return (
    <div className='PokemonList'>
      <div>
        <button onClick={goToPrev}>Previous</button>
        <button onClick={handlePrevPageSet}>{'<<'}</button>
        {pageNumbers.map((i) => {
          return indxNumbers.map((y) => {
            if (y === i && i >= pageIndx) {
              return <div key={y}>{y}</div>;
            }
            return null; 
          });
        })}
        <button onClick={goToNext}>Next</button>
        <button onClick={handleNextPageSet}>{'>>'}</button>
      </div>

      <div className='pokedex'>
        {pokedex.map(p => (
          <div className={`pokedex-item ${p.types[0].type.name}`} key={p.name} onClick={() => setPokemonData(p.name)}>
            <h3>{capitalizeLetter(p.name)}</h3>
            <img src={p.sprites.front_default} alt={p.name} />
            <div className='type-container'>
              {p.types.map(types => (
                <Type type={types.type.name} key={types.type.name} />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
