import { useState, useEffect } from 'react';
import axios from 'axios';

export default function Search({ handleSearch }) {
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSearchInput = (e) => {
    setName(e.target.value);
  };

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        setLoading(true);

        if (name === '') {
          const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=20');
          const pokemonResults = response.data.results;
          const pokemonDataPromises = pokemonResults.map((result) => axios.get(result.url));
          const pokemonDataResponses = await Promise.all(pokemonDataPromises);
          const pokemonData = pokemonDataResponses.map((res) => res.data);
          handleSearch(pokemonData);
        } else {
          handleSearch(name);
        }

        setLoading(false);
      } catch (error) {
        console.log('Error:', error.message);
        setLoading(false);
      }
    };

    fetchPokemonData();
  }, [name, handleSearch]);

  return (
    <div>
      <div>
        <div>
          <input value={name} onChange={(e) => {
            const searchValue = e.target.value;
            setName(searchValue);
            handleSearch(searchValue);
          }}></input>
        </div>
        <div>
          <h3>Primary Type: </h3>
          <select value={primaryType} onChange={handlePrimaryType}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Secondary Type: </h3>
          <select value={secondaryType} onChange={handleSecondaryType}>
            {types.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
        <div>
          <h3>Generation: </h3>
          <select value={generation} onChange={handleGeneration}>
            {generations.map((gen) => (
              <option key={gen}>{gen}</option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
}
