import React from 'react'

import capitalizeLetter from '../utils';
const secondType = pokemon.types[1].type.url

export default function Pokemon({ pokemon, pokedex }) {
  return (
    <div>
      <h2>{capitalizeLetter(pokemon.name)}</h2>
      <img src={pokemon.sprites.front_default}/>
      <div>
        <p>Type</p>
        <p>{pokemon.types[0].type.url}</p>
        {secondType ? secondType : null}
      </div>
    </div>
  )
}
