import React, { useEffect, useState } from "react";

export default function PokedexEntry({ species }) {
  const [pokedexEntries, setPokedexEntries] = useState([]);

  useEffect(() => {
    const fetchPokedexEntries = async () => {
      try {
        const response = await fetch(species);
        const data = await response.json();
        setPokedexEntries(data.flavor_text_entries);
      } catch (error) {
        console.log("Error fetching Pokedex entries:", error);
      }
    };

    fetchPokedexEntries();
  }, [species]);

  return (
    <div>
      <h2>Pokedex Entry</h2>
      {pokedexEntries.map((entry, index) => (
        <p key={index}>{entry.flavor_text}</p>
      ))}
    </div>
  );
}
