import { useState } from 'react';
import { capitalizeLetter } from '../../utils';
import Type from '../../components/Type';

export default function Pokedex({ pokedex, setPokemonData }) {
  const [currentPage, setCurrentPage] = useState(1);
  const resultsPerPage = 20;
  const totalPages = Math.ceil(pokedex.length / resultsPerPage);
  const startIndex = currentPage * resultsPerPage;
  const endIndex = startIndex + resultsPerPage;

  const goToPrevPage = () => {
    setCurrentPage(prevPage => prevPage - 1);
  };

  const goToNextPage = () => {
    setCurrentPage(prevPage => prevPage + 1);
  };

  return (
    <div className='PokemonList'>
      <div>
        <button onClick={goToPrevPage} disabled={currentPage === 0}>
          Previous
        </button>
        <button onClick={goToNextPage} disabled={endIndex >= pokedex.length || currentPage === totalPages - 1}>
          Next
        </button>
      </div>
      <div className='pokedex'>
        {pokedex.slice(startIndex, endIndex).map(p => (
          <div className={`pokedex-item ${p.types[0].type.name}`} key={p.name} onClick={() => setPokemonData(p.name)}>
            <h3>{capitalizeLetter(p.name)}</h3>
            <img src={p.sprites.front_default} alt={p.name} />
            <div className='type-container'>
              {p.types.map(types => (
                <Type type={types.type.name} key={types.type.name} />
              ))}
            </div>
          </div>
        ))}
      </div>
      <div>
        Page: {currentPage + 1} / {totalPages}
      </div>
    </div>
  );
}
