import { useState } from 'react'

export default function Pokemon({ pokemon, prev, next }) {
  const [btnUrl, setBtnUrl] = useState('')

  const goToPrev = () => {
    setBtnUrl(prev)
  }

  const goToNext = () => {
    setBtnUrl(next)
  }

  return (
    <div>
      <div>
        <button onClick={goToPrev()}>Previous</button>
        <button onClick={goToNext()}>Next</button>
      </div>
        {pokemon.map(p => (
            <div id={p}>{p}</div>
        ))}
    </div>
  )
}
