import { useEffect, useState } from 'react';
import axios from 'axios';

import { capitalizeLetter } from '../../utils';

export default function Evolution({ pokemon, species }) {
  const [evolutionData, setEvolutionData] = useState(null);

  useEffect(() => {
    const fetchEvolutionData = async () => {
      try {
        const response = await axios.get(species.url);
        const newData = response.data;
        setEvolutionData(newData);
      } catch (error) {
        console.log('Error: ', error.message);
      }
    };

    fetchEvolutionData();
  }, [species]);

  const getEvolutionMethod = (evolution) => {
    const evolutionDetails = evolution.evolution_details;

    if (evolutionDetails.length > 0) {
      const methods = evolutionDetails.map((detail) => {
        const trigger = detail.trigger.name;

        if (trigger === 'level-up') {
          return `Level ${detail.min_level}`;
        } else if (trigger === 'use-item') {
          return `Item: ${capitalizeLetter(detail.item.name)}`;
        } else if (trigger === 'trade') {
          return 'Trade';
        } else if (trigger === 'friendship') {
          if (detail.time_of_day) {
            return 'Friendship (Daytime)';
          } else {
            return 'Friendship';
          }
        } else if (trigger === 'level-up' && detail.known_move_type) {
          const moveType = capitalizeLetter(detail.known_move_type.name);
          return `Learn ${moveType} move`;
        } else {
          return 'Unknown';
        }
      });

      return methods.join(', ');
    }

    return 'Unknown';
  };

  const renderEvolutionChain = (chain) => {
    const { species, evolves_to } = chain;
    const evolutionMethod = getEvolutionMethod(chain);

    return (
      <div key={species.name}>
        <h3>{capitalizeLetter(species.name)}</h3>
        <p>Evolution Method: {evolutionMethod}</p>
        {evolves_to.length > 0 && (
          <div>
            {evolves_to.map((evo) => renderEvolutionChain(evo))}
          </div>
        )}
      </div>
    );
  };

  return (
    <>
      <h2>Evolution Chain</h2>
      {evolutionData ? (
        evolutionData.chain ? (
          renderEvolutionChain(evolutionData.chain)
        ) : (
          <p>No Evolution</p>
        )
      ) : (
        <p>Loading evolution chain...</p>
      )}
    </>
  );
}
