import { useState, useEffect } from 'react';
import { capitalizeLetter } from '../../utils';
import Type from '../../components/Type';
import { useLocation, useHistory } from 'react-router-dom';

export default function Pokedex({ pokedex, setPokemonData, goToPrev, goToNext }) {
  const location = useLocation();
  const history = useHistory();
  const queryParams = new URLSearchParams(location.search);
  const initialPage = parseInt(queryParams.get('page')) || 1;
  const [page, setPage] = useState(initialPage);
  const totalPages = Math.ceil(1019 / pokedex.length);
  const pageNumbers = Array.from({ length: totalPages }, (_, index) => index + 1);

  useEffect(() => {
    queryParams.set('page', page);
    history.replace({ search: queryParams.toString() });
  }, [page, queryParams, history]);

  const handleNextClick = () => {
    setPage(page + 1);
    goToNext();
  }

  const handlePrevClick = () => {
    setPage(page - 1);
    goToPrev();
  }

  const handleNextPageSet = () => {
    setPage(page + 10);
  }

  const handlePrevPageSet = () => {
    setPage(page - 10);
  };

  console.log(page);

  return (
    <div className='PokemonList'>
      <div>
        <button onClick={handlePrevClick}>Previous</button>
        <button onClick={handlePrevPageSet}>{'<<'}</button>
        {/* {pageArray.map((pageNumber) => (
          <div key={pageNumber}>{pageNumber}</div>
        ))} */}
        <button type="button" onClick={handleNextClick}>Next</button>
        <button onClick={handleNextPageSet}>{'>>'}</button>
      </div>

      <div className='pokedex'>
        {pokedex.map(p => (
          <div className={`pokedex-item ${p.types[0].type.name}`} key={p.name} onClick={() => setPokemonData(p.name)}>
            <h3>{capitalizeLetter(p.name)}</h3>
            <img src={p.sprites.front_default} alt={p.name} />
            <div className='type-container'>
              {p.types.map(types => (
                <Type type={types.type.name} key={types.type.name} />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
