import { useState } from 'react'

import { capitalizeLetter } from '../../utils';

import TypeContainer from '../../components/TypeContainer';

export default function Pokedex({ pokedex, setPokemonData, goToPrev, goToNext }) {

  return (
    <div className='PokemonList'>
      <div className='pokedex'>
          <button onClick={goToPrev}>Previous</button>
          <button onClick={goToNext}>Next</button>
      </div>
        {pokedex.map(p => (
            <div className='pokedex-item' key={p.name} onClick={() => setPokemonData(p.name)}>
              <h3>{capitalizeLetter(p.name)}</h3>
              <img src={p.sprites.front_default}/>
              {p.types.map(types => (
                <TypeContainer key={types.type.name} type={types.type.name} />
              ))}
            </div>
        ))}
    </div>
  )
}
