import React from 'react';
import capitalizeLetter from '../utils';

export default function Pokemon({ pokemon, formatGen }) {
  const secondType = pokemon && pokemon.types && pokemon.types.length > 1 ? pokemon.types[1].type.name : null;

  return (
    <div>
      <h2>{pokemon && capitalizeLetter(pokemon.name)}</h2>
      {pokemon && <img src={pokemon.sprites.front_default} alt={pokemon.name} />}
      {pokemon && (
        <div>
          <p>Type</p>
          <p>{pokemon.types[0].type.name}</p>
          {secondType && <p>{secondType}</p>}
        </div>
      )}
      <div>
        <button onClick={() => formatGen()}>Gen 1</button>
      </div>
    </div>
  );
}
