import { useEffect, useState } from 'react';
import axios from 'axios';

import { capitalizeLetter } from '../../utils';

export default function Evolution({ pokemon, species }) {
  const [evolutionData, setEvolutionData] = useState(null);

  useEffect(() => {
    const fetchEvolutionData = async () => {
      try {
        const response = await axios.get(species.evolution_chain.url);
        const newData = response.data;
        setEvolutionData(newData);
        console.log(newData);
      } catch (error) {
        console.log('Error: ', error.message);
      }
    };

    fetchEvolutionData();
  }, [species]);

  const renderEvolutionChain = (chain) => {
    return (
      <div>
        <div>
          {chain.evolves_to.map((evo) => (
            <div key={evo.species.name}>
              <h3>{capitalizeLetter(evo.species.name)}</h3>
              <PokemonImage imageUrl={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${evo.species.url.split('/')[6]}.png`} />
              {evo.evolves_to.length > 0 && renderEvolutionChain(evo)}
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <>
      <h2>Evolution Chain</h2>
      {evolutionData ? (
        evolutionData.chain ? (
          renderEvolutionChain(evolutionData.chain)
        ) : (
          <p>No Evolution</p>
        )
      ) : (
        <p>Loading evolution chain...</p>
      )}
    </>
  );
}

function PokemonImage({ imageUrl }) {
  const [imageData, setImageData] = useState(null);

  useEffect(() => {
    const fetchImageData = async () => {
      try {
        const response = await axios.get(imageUrl, { responseType: 'arraybuffer' });
        const base64Data = btoa(
          new Uint8Array(response.data).reduce((data, byte) => data + String.fromCharCode(byte), '')
        );
        const imageDataUrl = `data:image/jpeg;base64,${base64Data}`;
        setImageData(imageDataUrl);
      } catch (error) {
        console.log('Error: ', error.message);
      }
    };

    fetchImageData();
  }, [imageUrl]);

  return (
    <div>
      {imageData ? <img src={imageData} alt="Pokemon" /> : <p>Loading image...</p>}
    </div>
  );
}
