import { useState } from 'react';

export default function Search({ handleSearch, handlePageResults }) {
  const [name, setName] = useState('');

  const handleInput = (e) => {
    e.preventDefault();

    handleSearch(name)
  }

  return (
    <div>
      <form onSubmit={handleInput}>
        <input value={name} onChange={(e) => setName(e.target.value)}></input>
        <button className="submit-btn" type="submit">Search</button>
      </form>
       <div>
          <label>Results per page: </label>
          <select>
            <option>--Please choose an option--</option>
            <option onClick={() => handlePageResults(10)}>10</option>
            <option onClick={() => handlePageResults(20)}>20</option>
            <option onClick={() => handlePageResults(50)}>50</option>
            <option onClick={() => handlePageResults(100)}>100</option>
            <option>200</option>
            <option>All</option>
          </select>
      </div>
    </div>
    )
}
