import React, { useEffect, useState } from "react";

export default function Evolution({ pokemon }) {
  const [evolutionChain, setEvolutionChain] = useState([]);

  useEffect(() => {
    const fetchEvolutionChain = async () => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon-species/${pokemon}`);
        const data = await response.json();

        const evolutionUrl = data.evolution_chain.url;
        const evolutionResponse = await fetch(evolutionUrl);
        const evolutionData = await evolutionResponse.json();

        const chain = parseEvolutionChain(evolutionData.chain);
        setEvolutionChain(chain);
      } catch (error) {
        console.log("Error fetching evolution chain:", error);
      }
    };

    fetchEvolutionChain();
  }, [pokemon]);

  const parseEvolutionChain = (evolutionData) => {
    const chain = [];
    parseEvolutionData(evolutionData, chain);
    return chain;
  };

  const parseEvolutionData = (evolutionData, chain, isFirstEvolution = true) => {
    const name = evolutionData.species.name;
    const imageUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${evolutionData.species.url.split("/").slice(-2, -1)}.png`;
    let evolutionMethod = null;
    let evolutionDetails = evolutionData.evolution_details[0];

    if (!isFirstEvolution) {
      if (evolutionDetails.trigger.name === "level-up") {
        evolutionMethod = `Level up`;
      } else if (evolutionDetails.trigger.name === "use-item") {
        evolutionMethod = `Use ${evolutionDetails.item.name}`;
      } else if (evolutionDetails.trigger.name === "trade") {
        evolutionMethod = `Trade with another Pokémon`;
      } else if (evolutionDetails.trigger.name === "use-item-while-holding") {
        evolutionMethod = `Level up while holding ${evolutionDetails.item.name}`;
      } else if (evolutionDetails.trigger.name === "shed") {
        evolutionMethod = `Shed at level ${evolutionDetails.min_level}`;
      } else {
        evolutionMethod = `Method: ${evolutionDetails.trigger.name}`;
      }
    }

    chain.push({ name, imageUrl, evolutionMethod });

    if (evolutionData.evolves_to.length > 0) {
      evolutionData.evolves_to.forEach((evolvesTo) => {
        parseEvolutionData(evolvesTo, chain, false);
      });
    }
  };

  return (
    <div>
      <h2>Evolution Chain for {pokemon}</h2>
      <ul>
        {evolutionChain.map((pokemonData, index) => (
          <li key={index}>
            <img src={pokemonData.imageUrl} alt={pokemonData.name} />
            <span>{pokemonData.name}</span>
            {pokemonData.evolutionMethod && (
              <span> - Evolves by {pokemonData.evolutionMethod}</span>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
