export function capitalizeLetter(string) {
  return string.charAt(0).toUpperCase() + string.slice(1);
}

export function formatUnit(unit) {
  return (unit * 0.1).toFixed(1);
}

export function formatFeet(h) {
  const meters = (h * 0.1).toFixed(1);
  const totalInches = meters * 39.37;
  const feet = Math.floor(totalInches / 12);
  const inches = Math.round(totalInches % 12);

  return { feet, inches };
}

export function formatPounds(w) {
  const kilogram = (w * 0.1).toFixed(1);

  return (kilogram * 2.2).toFixed(1);
}

export default {
  capitalizeLetter,
  formatUnit,
  formatFeet,
  formatPounds,
};
