import axios from 'axios';
import { useState, useEffect } from 'react';
import capitalizeLetter from '../utils';

export default function Pokemon({ pokemon, formatHome }) {
  const [species, setSpecies] = useState(null)

  const secondType = pokemon.types.length > 1 ? pokemon.types[1].type.name : null;

  const formatUnit= (unit) => {
    return (unit * 0.1).toFixed(1)
  }

  const formatFeet = (h) => {
    const meters = (h * 0.1).toFixed(1)
    const totalInches = meters * 39.37; 
    const feet = Math.floor(totalInches / 12); 
    const inches = Math.round(totalInches % 12); 

    return { feet, inches };
  }

  const formatPounds = (w) => {
    const kilogram = (w * 0.1).toFixed(1)

    return (kilogram * 2.2).toFixed(1)
  }


  return (
    <div>
      <h2>{capitalizeLetter(pokemon.name)}</h2>
      <p>Pokedex: {pokemon.id}</p>
      <div>
        <img src={pokemon.sprites.front_default} alt={pokemon.name} />
        <img src={pokemon.sprites.front_shiny} alt={pokemon.name} />
      </div>
      <div>
        <div>
          <h3>Type</h3>
          <p>{capitalizeLetter(pokemon.types[0].type.name)}</p>
          {secondType && <p>{capitalizeLetter(secondType)}</p>}
        </div>
        <div>
          <h3>Classification</h3>
          <p>{species && species.genera.find((genus) => genus.language.name === 'en').genus}</p>
        </div>
        <div>
          <h3>Abilities</h3>
          {pokemon.abilities.map(ab => (!ab.is_hidden ? <p key={ab.ability.name}>{capitalizeLetter(ab.ability.name)}</p> : <p key={ab.ability.name}>{capitalizeLetter(ab.ability.name)}<span> (Hidden)</span></p>))}
        </div>
      </div>
      <div>
        <div>
          <h3>Height</h3>
          <p>{formatFeet(pokemon.height).feet}' {formatFeet(pokemon.height).inches}"</p>
          <p>{formatUnit(pokemon.height)}m</p>
        </div>
        <div>
          <h3>Weight</h3>
          <p>{formatPounds(pokemon.weight)}lbs</p>
          <p>{formatUnit(pokemon.weight)}kg</p>
        </div>
        <div>

        </div>
      </div>
      <button onClick={formatHome}>Back</button>
    </div>
  );
}
