import React, { useState, useEffect } from 'react';
import axios from 'axios';

import PokemonList from './components/PokemonListData/PokemonList';
import PokemonData from './components/PokemonData/Pokemon';
import Search from './components/Search';
import './App.css';

function App() {
  const [url, setUrl] = useState('https://pokeapi.co/api/v2/pokemon/')
  const [pokedex, setPokedex] = useState([]);
  const [pokemon, setPokemon] = useState(null);
  const [species, setSpecies] = useState(null);
  const [name, setName] = useState('')
  const [loading, setLoading] = useState(true);
  const [next, setNext] = useState('');
  const [prev, setPrev] = useState('');
  const [filteredPokemon, setFilteredPokemon] = useState([]);

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(url);
        const pokemonResults = response.data.results;
        const pokemonDataPromises = pokemonResults.map((result) => axios.get(result.url));
        const pokemonDataResponses = await Promise.all(pokemonDataPromises);
        const pokemonData = pokemonDataResponses.map((res) => res.data);
        setPokedex(pokemonData);
        setLoading(false);
        setPrev(response.data.previous)
        setNext(response.data.next)
        console.log(pokemonData);
      }
       catch (error) {
        console.log('Error:', error.message);
        setLoading(false);
      }
    };

    fetchPokemonData();
  }, [url]);

  const setPokemonData = async (pokemonName) => {
    try {
      setLoading(true);
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`);
      const pokemonData = response.data;
      setPokemon(pokemonData);
      setLoading(false);
    } catch (error) {
      console.log('Error:', error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    const fetchNewData = async (data) => {
      try {
        if (pokemon && pokemon.species ) {
          const response = await axios.get(pokemon.species.url);
          const speciesData = response.data;
          setSpecies(speciesData);
          console.log(speciesData);
        }
      } catch (error) {
        console.log('Error: ', error.message);
      }
    };

    fetchNewData();
  }, [pokemon]);

  const formatSearch = () => {
    setFilteredPokemon([]);
    setPokemon(null);
  };

  const handleSearch = async (name) => {
    try {
      setLoading(true);
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${name}`);
      const pokemonData = response.data;
      setFilteredPokemon([pokemonData]);
      setLoading(false);
    } catch (error) {
      console.log('Error:', error.message);
      setFilteredPokemon([]);
      setLoading(false);
    }
  };

  const goToPrev = () => {
    setUrl(prev)
  }

  const goToNext = () => {
      setUrl(next);
  }

  return (
    <div className="App">
      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          {pokemon ? (
            <PokemonData pokemon={pokemon} species={species} setPokemonData={setPokemonData} formatHome={formatSearch} />
          ) : (
            <div>
              <Search handleSearch={handleSearch} formatSearch={formatSearch} />
              <PokemonList pokedex={filteredPokemon.length > 0 ? filteredPokemon : pokedex} setPokemonData={setPokemonData} goToPrev={goToPrev} goToNext={goToNext} />
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
