import React, { useEffect, useState } from "react";

export default function PokedexEntry({ species }) {
  const [description, setDescription] = useState("");

  useEffect(() => {
    const fetchPokedexDescription = async () => {
      try {
        const response = await fetch(
          `https://pokeapi.co/api/v2/generation/${generation}`
        );
        const data = await response.json();
        const descriptionEntries = data.pokemon_species[0].flavor_text_entries;
        const englishDescription = descriptionEntries.find(
          (entry) => entry.language.name === "en"
        );
        setDescription(englishDescription.flavor_text);
      } catch (error) {
        console.log("Error fetching Pokedex description:", error);
      }
    };

    fetchPokedexDescription();
  }, [generation]);

  return (
    <div>
      <h2>Pokedex Entry - Generation {generation}</h2>
      <p>{description}</p>
    </div>
  );
};
